mapper.select:
select count(1)
from t_receipt tr

    ,t_customer tc


    ,t_indent ti



        and tr.chargeman_id = #{chargemanId}


        and tr.cust_id = #{custId}


        and tr.cust_id = tc.cust_id
        and tc.cust_name like concat('%',#{custName},'%')


        and tr.busi_no = ti.indent_no
        and ti.salesman_id = #{salesmanId}


        and tr.stat = #{stat}


        and tr.receipt_no like concat('%', #{receiptNo},'%')


        and tr.account_type = #{accountType}


        and tr.create_time between #{beginTime} and #{endTime}


        and tr.payway like concat('%',#{payway},'%')


        and tr.type = #{type}


        and tr.cust_id in (
          select cust_id from t_customer
          where area_grp_id in (
            select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
          )
        )

select

from t_receipt tr

    ,t_customer tc


    ,t_indent ti



        and tr.chargeman_id = #{chargemanId}


        and tr.cust_id = #{custId}


        and tr.cust_id = tc.cust_id
        and tc.cust_name like concat('%',#{custName},'%')


        and tr.busi_no = ti.indent_no
        and ti.salesman_id = #{salesmanId}


        and tr.stat = #{stat}


        and tr.receipt_no like concat('%', #{receiptNo},'%')


        and tr.account_type = #{accountType}


        and tr.create_time between #{beginTime} and #{endTime}


        and tr.payway like concat('%',#{payway},'%')


        and tr.type = #{type}


        and tr.cust_id in (
            select cust_id from t_customer
            where area_grp_id in (
              select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
            )
        )


order by tr.create_time desc,tr.receipt_id desc

select

from t_receipt tr
where tr.receipt_id = #{receiptId}

select

from t_receipt tr
where tr.busi_no = #{busiNo}

SELECT
IFNULL(sum(IF(tr.type = '收款单' ,cast(tr.receipt_amount as decimal(9,2)),0)),0)  AS receivedAmountTotal,
IFNULL(sum(IF(tr.type = '付款单' ,cast(tr.receipt_amount as decimal(9,2)),0)),0)  AS payedAmountTotal
FROM (
SELECT cust_id FROM t_customer


        AND cust_name LIKE concat('%',#{custName},'%')


        AND price_grp_id = #{priceGrpId}



            #{custId}




            #{areaGrpId}



) as tc
LEFT JOIN (
select cust_id
from t_indent
where stat in ('已完成','已出库')
and indent_time between #{beginTime} and #{endTime}


        AND salesman_id = #{salesmanId}


group by cust_id
) AS ti
ON tc.cust_id = ti.cust_id
LEFT JOIN (select * from t_receipt where create_time between #{beginTime} and #{endTime} and stat = 1) AS tr
ON tc.cust_id = tr.cust_id

SELECT
tc.cust_id AS custId,
tc.cust_no AS custNo,
tc.cust_name AS custName,
abs(
ifnull(
sum(
if(
tdd.oper = '增加欠款'
or substring(tdd.name_no,1,3) = '订货单'
or
(substring(tdd.name_no,1,3) = '调账单'and tdd.oper = '调账增加'),
cast(tdd.amount as decimal(9,2)),
0
)
)
,0
)
) as salesAmount,
abs(
ifnull(
sum(
if(
substring(tdd.name_no,1,3) = '收款单' ,
cast(tdd.amount as decimal(9,2)),
0
)
) ,
0
)
) as receivedAmount,

abs(
ifnull(
sum(
if(
substring(tdd.name_no,1,3) = '付款单'
or
substring(tdd.name_no,1,3) = '退货单'
or
(substring(tdd.name_no,1,3) = '调账单' and substring(tdd.oper,1,4) = '调账减少'),
cast(tdd.amount as decimal(9,2)),
0
)
),
0
)
) as payedAmount
FROM(
select * from t_customer



              #{custId}



          and area_grp_id in (
            select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
          )


          and cust_name like concat('%',#{custName},'%')


          and price_grp_id = #{priceGrpId}


) as tc
LEFT JOIN(
  select * from t_deal_detail where deal_time between #{beginTime} and #{endTime}
) as tdd
on tdd.cust_id = tc.cust_id
GROUP BY tc.cust_id
ORDER BY tc.cust_no

SELECT count(1) FROM(
select * from t_customer



            #{custId}



        and area_grp_id in (
          select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
        )


        and cust_name LIKE concat('%',#{custName},'%')


        and price_grp_id = #{priceGrpId}


) as tc
LEFT JOIN(
  select * from t_deal_detail where deal_time between #{beginTime} and #{endTime}
) as tdd
on tdd.cust_id = tc.cust_id
GROUP BY tc.cust_id

SELECT COUNT(1) FROM t_receipt tr
WHERE tr.cust_id = #{custId}

    AND tr.create_time BETWEEN #{beginTime} and #{endTime}

select ifnull(sum(if(tr.type = '收款单' and tr.stat = 1,cast(tr.receipt_amount as decimal(9,2)),0)),0) as receiptedDetb,
       ifnull(sum(if(tr.type = '付款单' and tr.stat = 1,cast(tr.receipt_amount as decimal(9,2)),0)),0) as cutDebt
from t_receipt tr
where tr.stat = 1
and tr.cust_id = #{custId}


        AND tr.create_time BETWEEN #{beginTime} AND #{endTime}


        AND tr.account_type = #{oper}

SELECT

concat(cast(sum(if(tr.payway = #{payway},tr.receipt_amount,0)) as decimal(9,2)),'') as #{payway}

FROM t_receipt tr

    ,t_customer tc


    ,t_indent ti

where tr.type = #{type}
and tr.stat = 1
and tr.account_type = #{accountType}

    and tr.create_time between #{beginTime} and #{endTime}


    and ti.indent_no = tr.busi_no
    and ti.salesman_id = #{salesmanId}
    and ti.stat in ('已出库','已完成')



        #{custId}



    and tc.cust_id = tr.cust_id
    and tc.area_grp_id in (
      select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
    )

SELECT count(distinct account_type)
FROM t_receipt tr

    ,t_customer tc


    ,t_indent ti

where tr.type = #{type}
and tr.stat = 1
and tr.account_type = #{accountType}

    and tr.create_time between #{beginTime} and #{endTime}


    and ti.indent_no = tr.busi_no
    and ti.salesman_id = #{salesmanId}
    and ti.stat in ('已出库','已完成')



        #{custId}



    and tc.cust_id = tr.cust_id
    and tc.area_grp_id in (
      select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
    )

SELECT abs( ifnull( sum( if( tdd.oper = '增加欠款' or substring(tdd.name_no,1,3) = '订货单' or (substring(tdd.name_no,1,3) = '调账单'and tdd.oper = '调账增加') ,cast(tdd.amount as decimal(9,2)) ,0) ) ,0) ) as salesAmountTotal, abs(ifnull( sum( if(substring(tdd.name_no,1,3) = '收款单' , cast(tdd.amount as decimal(9,2)),0) ) ,0)) as receivedAmountTotal, abs( ifnull( sum( if( substring(tdd.name_no,1,3) = '付款单' or substring(tdd.name_no,1,3) = '退货单' or (substring(tdd.name_no,1,3) = '调账单' and substring(tdd.oper,1,4) = '调账减少'), cast(tdd.amount as decimal(9,2)) ,0) ) ,0) ) as payedAmountTotal from( select * from t_customer
#{custId}
AND area_grp_id IN ( select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId})) )
AND cust_name LIKE concat('%',#{custName},'%')
AND price_grp_id = #{priceGrpId}
) as tc left join(select * from t_deal_detail where deal_time between #{beginTime} and #{endTime}) as tdd on tdd.cust_id = tc.cust_id

select ifnull(sum( tr.receipt_amount ),0) as receiptSum
from t_receipt tr

    ,t_customer tc


    ,t_indent ti



        and tr.chargeman_id = #{chargemanId}


        and tr.cust_id = #{custId}


        and tr.cust_id = tc.cust_id
        and tc.cust_name like concat('%',#{custName},'%')


        and tr.busi_no = ti.indent_no
        and ti.salesman_id = #{salesmanId}


        and tr.stat = #{stat}


        and tr.receipt_no like concat('%', #{receiptNo},'%')


        and tr.account_type = #{accountType}


        and tr.create_time between #{beginTime} and #{endTime}


        and tr.payway like concat('%',#{payway},'%')


        and tr.type = #{type}


        and tr.cust_id in (
          select cust_id from t_customer
          where area_grp_id in (
            select area_grp_id from t_area_grp where FIND_IN_SET(area_grp_id,getChildList(#{areaGrpId}))
          )
        )
